mapper.select:
SELECT * FROM ( SELECT T1.* , ROWNUM rm , COUNT(PRO_NO) OVER() AS tot_cont FROM ( SELECT PRO_NO , PRO_DISPRICE , TO_CHAR(PRO_DISPRICE, '999,999,999,999')||'원' PRO_CH_PRICE , PCL_NAME, PRO_PCL_NO, PRO_STOCKCOUNT , RPAD(PRO_NAME, 60, ' ') AS PRO_NAME FROM PRODUCT A JOIN PRO_CLASS B ON A.PRO_PCL_NO = B.PCL_NO
WHERE PRO_PCL_NO = #{pro_pcl_no}
ORDER BY PRO_NO DESC ) T1 ) T2 WHERE rm BETWEEN #{begin} AND #{end}

SELECT 
PMG_FILE FROM PRO_IMG
WHERE PMG_PRO_NO = #{pro_no}
AND PMG_IDT_NO = #{idt_no}

mapper.update:
UPDATE 
PRODUCT SET
PRO_NAME = #{pro_name}
, PRO_REALPRICE = #{pro_disprice}
, PRO_DISPRICE= #{pro_disprice}
, PRO_MILEGE = FLOOR(#{pro_disprice}*0.005) 
, PRO_MKR_NO = #{pro_mkr_no}
, PRO_STOCKCOUNT = #{pro_stockcount}
WHERE PRO_NO = #{pro_no}

UPDATE 
PRO_SUMM SET
PSM_CONENT = #{psm_conent}
WHERE PSM_PRO_NO = #{pro_no}

UPDATE 
PRO_IMG SET
PMG_FILE = #{pmg_file}
WHERE PMG_PRO_NO = #{pmg_pro_no} AND PMG_IDT_NO = #{pmg_idt_no}
